import React from "react";
import "./index.css";

export interface Product {
  id: number;
  name: string;
  price: number;
  image: string;
}

interface ProductsProps {
  onAdd: (product: Product) => void;
}

const mockProducts: Product[] = [
  { id: 1, name: "Laptop", price: 60000, image: "data:image/jpeg;base64," },
  { id: 2, name: "Mobile", price: 30000, image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQoqN6vRWAk1LPIV5hiO8hyjGXeKQAyH19cPw&s" },
  { id: 3, name: "Tablet", price: 20000, image: "data:image/jpeg;base64," },
  { id: 4, name: "Smartwatch", price: 5000, image: "data:image/jpeg;base64," }
];

const Products: React.FC<ProductsProps> = ({ onAdd }) => {
  return (
    <div className="products-container">
      {mockProducts.map((product) => (
        <div key={product.id} className="product-card">
          <img src={product.image} alt={product.name} />
          <h3>{product.name}</h3>
          <p>â‚¹{product.price}</p>
          <button onClick={() => onAdd(product)}>Add to Cart</button>
        </div>
      ))}
    </div>
  );
};

export default Products;
